            },
            accentHSL: {
                H: 0,
                S: 0,
                L: 0

            },
            neutralHSL: {
                H: 0,
                S: 0,
                L: 0

            },
            darkenValue: 10,
            lightenValue: 50,
            rotateValue: 0,
            saturationValue: 10,
            colorPalettes: [],
            currentPalette: [],
            currentPaletteName: 'Awesome',
            currentPaletteNames: [],
