    },
    mounted() {
        

        


        (this.headingSerif === 1) ? SetRootProperty('--cardHeading', 'var(--fontSerif)') : SetRootProperty('--cardHeading', 'var(--fontSansSerif)');

        // Need to create an instance of modal to programically close modal with set timeout
